                      <input
                        type="password"
                        name="password"
                        id="password"
                        className="form-control"
                        placeholder="password"
                      />
                    </div>

                    <div className="field-set">
                      <input
                        type="submit"
                        id="send_message"
                        value="Submit"
                        className="btn btn-main btn-fullwidth color-2"
                      />
                    </div>

                    <div className="clearfix"></div>

                    <div className="spacer-single"></div>
                    <ul className="list s3">
                      <li>Login with:</li>
                      <li>
