};
/**
Adds the callback and sets up the methods, to iterate over the results.

@method pollFilter
@param {Object} self
*/


var pollFilter = function (self) {
  var onMessage = function (error, messages) {
    if (error) {
      return self.callbacks.forEach(function (callback) {
        callback(error);
      });
    }

    if (utils.isArray(messages)) {
      messages.forEach(function (message) {
        message = self.formatter ? self.formatter(message) : message;
        self.callbacks.forEach(function (callback) {
          callback(null, message);
        });
      });
    }
  };

  self.requestManager.startPolling({
    method: self.implementation.poll.call,
    params: [self.filterId]
  }, self.filterId, onMessage, self.stopWatching.bind(self));
};

var Filter = function (options, type, requestManager, methods, formatter, callback, filterCreationErrorCallback) {
  var self = this;
  var implementation = {};
  methods.forEach(function (method) {
    method.setRequestManager(requestManager);
    method.attachToObject(implementation);
  });
  this.requestManager = requestManager;
  this.options = getOptions(options, type);
  this.implementation = implementation;
  this.filterId = null;
  this.callbacks = [];
  this.getLogsCallbacks = [];
  this.pollFilters = [];
  this.formatter = formatter;
  this.implementation.newFilter(this.options, function (error, id) {
    if (error) {
      self.callbacks.forEach(function (cb) {
        cb(error);
      });

      if (typeof filterCreationErrorCallback === 'function') {
        filterCreationErrorCallback(error);
      }
    } else {
      self.filterId = id; // check if there are get pending callbacks as a consequence
      // of calling get() with filterId unassigned.

      self.getLogsCallbacks.forEach(function (cb) {
        self.get(cb);
      });
      self.getLogsCallbacks = []; // get filter logs for the already existing watch calls

      self.callbacks.forEach(function (cb) {
        getLogsAtStart(self, cb);
      });
      if (self.callbacks.length > 0) pollFilter(self); // start to watch immediately

      if (typeof callback === 'function') {
        return self.watch(callback);
      }
    }
  });
  return this;
};

Filter.prototype.watch = function (callback) {
  this.callbacks.push(callback);

  if (this.filterId) {
    getLogsAtStart(this, callback);
    pollFilter(this);
  }

  return this;
};

Filter.prototype.stopWatching = function (callback) {
  this.requestManager.stopPolling(this.filterId);
  this.callbacks = []; // remove filter async

  if (callback) {
    this.implementation.uninstallFilter(this.filterId, callback);
  } else {
    return this.implementation.uninstallFilter(this.filterId);
  }
};

Filter.prototype.get = function (callback) {
  var self = this;

  if (utils.isFunction(callback)) {
    if (this.filterId === null) {
      // If filterId is not set yet, call it back
      // when newFilter() assigns it.
      this.getLogsCallbacks.push(callback);
    } else {
      this.implementation.getLogs(this.filterId, function (err, res) {
        if (err) {
          callback(err);
        } else {
          callback(null, res.map(function (log) {
            return self.formatter ? self.formatter(log) : log;
          }));
        }
      });
    }
  } else {
    if (this.filterId === null) {
      throw new Error('Filter ID Error: filter().get() can\'t be chained synchronous, please provide a callback for the get() method.');
    }

    var logs = this.implementation.getLogs(this.filterId);
    return logs.map(function (log) {
      return self.formatter ? self.formatter(log) : log;
    });
  }

  return this;
};

module.exports = Filter;

